import React, {Component} from "react";



class App extends Component
{

  state =
  {
    counter: 0
  }

  render ()
  {
    //console.log
    return(
      <div>
        <h1> counter: {this.state.counter} </h1>

        <button onClick={() => this.setState({counter: this.state.counter + 1})}  >
          incrementa
        </button>

        <button onClick={() => this.setState({counter: this.state.counter - 1})}>
          decrementa
        </button>
      </div>
    );
  }
}

export default App;
